import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import './About.css';

const About = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut'
      }
    }
  };

  const skills = [
    { name: 'React', level: 95 },
    { name: 'Node.js', level: 90 },
    { name: 'Python', level: 88 },
    { name: 'Machine Learning', level: 85 },
    { name: 'TypeScript', level: 82 },
    { name: 'AWS', level: 80 }
  ];

  const experiences = [
    {
      title: 'Software Engineer',
      company: 'Tech Company',
      period: '2022 - Present',
      description: 'Building scalable web applications and leading technical initiatives.'
    },
    {
      title: 'ML Engineer',
      company: 'AI Startup',
      period: '2021 - 2022',
      description: 'Developed machine learning models for predictive analytics and data insights.'
    },
    {
      title: 'Quantitative Analyst',
      company: 'Finance Corp',
      period: '2020 - 2021',
      description: 'Applied mathematical models for risk assessment and algorithmic trading strategies.'
    }
  ];

  return (
    <section id="about" className="about" ref={ref}>
      <div className="about-container">
        <motion.div
          className="about-content"
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <motion.div className="about-header" variants={itemVariants}>
            <h2 className="section-title">About Me</h2>
            <p className="section-subtitle">
              Passionate about creating innovative solutions through technology
            </p>
          </motion.div>

          <div className="about-grid">
            <motion.div className="about-text" variants={itemVariants}>
              <h3>My Journey</h3>
              <p>
                I'm a software engineer passionate about building scalable systems and data-driven products. 
                I combine applied mathematics, software engineering, and finance to solve real-world problems 
                and create meaningful impact through technology.
              </p>
              <p>
                With expertise spanning full-stack development, machine learning, and quantitative analysis, 
                I enjoy tackling complex challenges and turning ideas into reality. My approach is always 
                data-driven and user-focused.
              </p>
            </motion.div>

            <motion.div className="skills-section" variants={itemVariants}>
              <h3>Technical Skills</h3>
              <div className="skills-grid">
                {skills.map((skill, index) => (
                  <motion.div
                    key={skill.name}
                    className="skill-item"
                    initial={{ opacity: 0, x: -20 }}
                    animate={inView ? { opacity: 1, x: 0 } : { opacity: 0, x: -20 }}
                    transition={{ delay: index * 0.1 + 0.3 }}
                  >
                    <div className="skill-header">
                      <span className="skill-name">{skill.name}</span>
                      <span className="skill-percentage">{skill.level}%</span>
                    </div>
                    <div className="skill-bar">
                      <motion.div
                        className="skill-progress"
                        initial={{ width: 0 }}
                        animate={inView ? { width: `${skill.level}%` } : { width: 0 }}
                        transition={{ delay: index * 0.1 + 0.5, duration: 1 }}
                      />
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </div>

          <motion.div className="experience-section" variants={itemVariants}>
            <h3>Professional Experience</h3>
            <div className="experience-timeline">
              {experiences.map((exp, index) => (
                <motion.div
                  key={index}
                  className="experience-item"
                  initial={{ opacity: 0, x: -30 }}
                  animate={inView ? { opacity: 1, x: 0 } : { opacity: 0, x: -30 }}
                  transition={{ delay: index * 0.2 + 0.4 }}
                >
                  <div className="experience-dot" />
                  <div className="experience-content">
                    <h4>{exp.title}</h4>
                    <p className="experience-company">{exp.company}</p>
                    <p className="experience-period">{exp.period}</p>
                    <p className="experience-description">{exp.description}</p>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
