import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import './About.css';

const About = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut'
      }
    }
  };

  const skills = [
    { name: 'Python', icon: 'üêç' },
    { name: 'C++', icon: '‚ö°' },
    { name: 'Java', icon: '‚òï' },
    { name: 'JavaScript', icon: 'üü®' },
    { name: 'React', icon: '‚öõÔ∏è' },
    { name: 'Node.js', icon: 'üü¢' },
    { name: 'SQL', icon: 'üóÑÔ∏è' },
    { name: 'Git', icon: 'üì¶' }
  ];

  const interests = [
    { name: 'AI', icon: 'ü§ñ' },
    { name: 'Cybersecurity', icon: 'üîí' },
    { name: 'Software Engineering', icon: 'üíª' },
    { name: 'Finance', icon: 'üí∞' }
  ];

  const hobbies = [
    { name: 'R&B Music', icon: 'üéµ' },
    { name: 'Basketball', icon: 'üèÄ' },
    { name: 'Soccer', icon: '‚öΩ' },
    { name: 'Chess', icon: '‚ôüÔ∏è' },
    { name: 'Weightlifting', icon: 'üèãÔ∏è' },
    { name: 'Hiking', icon: 'ü•æ' }
  ];

  return (
    <section id="about" className="about" ref={ref}>
      <div className="about-container">
        <motion.div
          className="about-content"
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <motion.div className="about-header" variants={itemVariants}>
            <h2 className="section-title">About Me</h2>
            <p className="section-subtitle">
              Passionate about creating innovative solutions through technology
            </p>
          </motion.div>

          <div className="about-grid">
            <motion.div className="about-text" variants={itemVariants}>
              <div className="profile-section">
                <div className="profile-image">
                  <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=400&fit=crop&crop=face" alt="Atul Kamath" />
                  <div className="profile-badge profile-badge-1">25+</div>
                  <div className="profile-badge profile-badge-2">UCB</div>
                </div>
                <div className="profile-info">
                  <h3>My Journey</h3>
                  <p>
                I'm a Computer Science and Applied Mathematics student at UC Berkeley, passionate about building scalable systems and data-driven products. 
                I combine applied mathematics, software engineering, and finance to solve real-world problems 
                and create meaningful impact through technology.
              </p>
              <p>
                With interests spanning AI, cybersecurity, software engineering, and finance, 
                I enjoy tackling complex challenges and turning ideas into reality. My approach is always 
                data-driven and user-focused.
              </p>
                </div>
              </div>
            </motion.div>

            <motion.div className="skills-section" variants={itemVariants}>
              <h3>Technical Skills</h3>
              <div className="skills-grid">
                {skills.map((skill, index) => (
                  <motion.div
                    key={skill.name}
                    className="skill-item"
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={inView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                    transition={{ delay: index * 0.1 + 0.3 }}
                    whileHover={{ scale: 1.1, rotate: 5 }}
                  >
                    <div className="skill-icon">{skill.icon}</div>
                    <span className="skill-name">{skill.name}</span>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </div>

          <motion.div className="interests-section" variants={itemVariants}>
            <h3>My Interests</h3>
            <div className="interests-grid">
              {interests.map((interest, index) => (
                <motion.div
                  key={interest.name}
                  className="interest-item"
                  initial={{ opacity: 0, y: 20 }}
                  animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
                  transition={{ delay: index * 0.1 + 0.4 }}
                  whileHover={{ scale: 1.05 }}
                >
                  <div className="interest-icon">{interest.icon}</div>
                  <span className="interest-name">{interest.name}</span>
                </motion.div>
              ))}
            </div>
          </motion.div>

          <motion.div className="metrics-section" variants={itemVariants}>
            <h3>Key Metrics</h3>
            <div className="metrics-grid">
              <motion.div 
                className="metric-card"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={inView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                transition={{ delay: 0.1 }}
                whileHover={{ scale: 1.05, rotate: 2 }}
              >
                <div className="metric-number">25+</div>
                <div className="metric-label">Projects Built</div>
              </motion.div>
              <motion.div 
                className="metric-card"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={inView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                transition={{ delay: 0.2 }}
                whileHover={{ scale: 1.05, rotate: -2 }}
              >
                <div className="metric-number">8</div>
                <div className="metric-label">Languages</div>
              </motion.div>
              <motion.div 
                className="metric-card"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={inView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                transition={{ delay: 0.3 }}
                whileHover={{ scale: 1.05, rotate: 2 }}
              >
                <div className="metric-number">UCB</div>
                <div className="metric-label">Berkeley CS</div>
              </motion.div>
              <motion.div 
                className="metric-card"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={inView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                transition={{ delay: 0.4 }}
                whileHover={{ scale: 1.05, rotate: -2 }}
              >
                <div className="metric-number">4</div>
                <div className="metric-label">Interests</div>
              </motion.div>
            </div>
          </motion.div>

          <motion.div className="hobbies-section" variants={itemVariants}>
            <h3>Hobbies & Activities</h3>
            <div className="hobbies-grid">
              {hobbies.map((hobby, index) => (
                <motion.div
                  key={hobby.name}
                  className="hobby-item"
                  initial={{ opacity: 0, y: 20 }}
                  animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
                  transition={{ delay: index * 0.1 + 0.5 }}
                  whileHover={{ scale: 1.05 }}
                >
                  <div className="hobby-icon">{hobby.icon}</div>
                  <span className="hobby-name">{hobby.name}</span>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
