import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import './About.css';

const About = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut'
      }
    }
  };

  const skills = [
    { name: 'Python', icon: 'üêç' },
    { name: 'C++', icon: '‚ö°' },
    { name: 'Java', icon: '‚òï' },
    { name: 'JavaScript', icon: 'üü®' },
    { name: 'React', icon: '‚öõÔ∏è' },
    { name: 'Node.js', icon: 'üü¢' },
    { name: 'SQL', icon: 'üóÑÔ∏è' },
    { name: 'Git', icon: 'üì¶' }
  ];

  const interests = [
    { name: 'AI', icon: 'ü§ñ' },
    { name: 'Cybersecurity', icon: 'üîí' },
    { name: 'Software Engineering', icon: 'üíª' },
    { name: 'Finance', icon: 'üí∞' }
  ];

  const hobbies = [
    { name: 'R&B Music', icon: 'üéµ' },
    { name: 'Basketball', icon: 'üèÄ' },
    { name: 'Soccer', icon: '‚öΩ' },
    { name: 'Chess', icon: '‚ôüÔ∏è' },
    { name: 'Weightlifting', icon: 'üèãÔ∏è' },
    { name: 'Hiking', icon: 'ü•æ' }
  ];

  return (
    <section id="about" className="about" ref={ref}>
      <div className="about-container">
        <motion.div
          className="about-content"
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <motion.div className="about-header" variants={itemVariants}>
            <h2 className="section-title">About Me</h2>
            <p className="section-subtitle">
              Passionate about creating innovative solutions through technology
            </p>
          </motion.div>

          <div className="about-grid">
            <motion.div className="about-text" variants={itemVariants}>
              <div className="profile-section">
                <div className="profile-image">
                  <img src="/api/placeholder/200/200" alt="Atul Kamath" />
                </div>
                <div className="profile-info">
                  <h3>My Journey</h3>
                  <p>
                I'm a Computer Science and Applied Mathematics student at UC Berkeley, passionate about building scalable systems and data-driven products. 
                I combine applied mathematics, software engineering, and finance to solve real-world problems 
                and create meaningful impact through technology.
              </p>
              <p>
                With interests spanning AI, cybersecurity, software engineering, and finance, 
                I enjoy tackling complex challenges and turning ideas into reality. My approach is always 
                data-driven and user-focused.
              </p>
                </div>
              </div>
            </motion.div>

            <motion.div className="skills-section" variants={itemVariants}>
              <h3>Technical Skills</h3>
              <div className="skills-grid">
                {skills.map((skill, index) => (
                  <motion.div
                    key={skill.name}
                    className="skill-item"
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={inView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                    transition={{ delay: index * 0.1 + 0.3 }}
                    whileHover={{ scale: 1.1, rotate: 5 }}
                  >
                    <div className="skill-icon">{skill.icon}</div>
                    <span className="skill-name">{skill.name}</span>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </div>

          <motion.div className="experience-section" variants={itemVariants}>
            <h3>Professional Experience</h3>
            <div className="experience-timeline">
              {experiences.map((exp, index) => (
                <motion.div
                  key={index}
                  className="experience-item"
                  initial={{ opacity: 0, x: -30 }}
                  animate={inView ? { opacity: 1, x: 0 } : { opacity: 0, x: -30 }}
                  transition={{ delay: index * 0.2 + 0.4 }}
                >
                  <div className="experience-dot" />
                  <div className="experience-content">
                    <h4>{exp.title}</h4>
                    <p className="experience-company">{exp.company}</p>
                    <p className="experience-period">{exp.period}</p>
                    <p className="experience-description">{exp.description}</p>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
